                'gallery' => 'product/dell_7480/1555643321dell_7480_3.jpg;product/dell_7480/1555643321dell_7480_2.png;product/dell_7480/1555643321dell_7480.jpg;product/dell_7480/1555643321Dell-latitude-7480-4.jpg',
                'status' => 1,
